const cards = document.getElementById('cards');
const searchInput = document.getElementById('search');
const chipButtons = document.querySelectorAll('.chip');

const createCard = (item) => {
  const el = document.createElement('article');
  el.className = 'card reveal';
  el.setAttribute('role','listitem');

  const h = document.createElement('h4');
  const a = document.createElement('a');
  a.href = item.url;
  a.target = '_blank';
  a.rel = 'noopener noreferrer';
  a.textContent = item.title;
  h.appendChild(a);

  const p = document.createElement('p');
  p.textContent = item.desc || item.url;

  const actions = document.createElement('div');
  actions.className = 'actions';

  const open = document.createElement('a');
  open.className = 'btn primary';
  open.href = item.url;
  open.target = '_blank';
  open.rel = 'noopener noreferrer';
  open.textContent = 'Open';

  const copy = document.createElement('button');
  copy.className = 'btn ghost';
  copy.type = 'button';
  copy.textContent = 'Copy';
  copy.addEventListener('click', (e) => {
    e.preventDefault();
    copyText(item.url);
  });

  actions.appendChild(open);
  actions.appendChild(copy);
  el.appendChild(h);
  el.appendChild(p);
  el.appendChild(actions);
  return el;
};

const renderCards = (list) => {
  cards.innerHTML = '';
  if (list.length === 0) {
    const empty = document.createElement('p');
    empty.textContent = 'No results found. Try adjusting your search or filter.';
    empty.style.gridColumn = '1 / -1';
    empty.style.textAlign = 'center';
    empty.style.color = 'var(--muted)';
    empty.style.padding = '40px 20px';
    cards.appendChild(empty);
    return;
  }
  list.forEach(it => cards.appendChild(createCard(it)));
  document.querySelectorAll('#cards .reveal').forEach(el => io.observe(el));
};

renderCards(PORTFOLIO);

const filterCards = () => {
  const q = searchInput.value.trim().toLowerCase();
  const active = document.querySelector('.chip.active').getAttribute('data-cat');
  const filtered = PORTFOLIO.filter(p => {
    const matchCat = active === 'all' || p.cat === active;
    const matchQ = !q || (
      (p.title || '').toLowerCase().includes(q) ||
      (p.desc || '').toLowerCase().includes(q) ||
      (p.url || '').toLowerCase().includes(q)
    );
    return matchCat && matchQ;
  });
  renderCards(filtered);
};

searchInput.addEventListener('input', filterCards);

chipButtons.forEach(btn => {
  btn.addEventListener('click', () => {
    chipButtons.forEach(b => {
      b.classList.remove('active');
      b.setAttribute('aria-pressed', 'false');
    });
    btn.classList.add('active');
    btn.setAttribute('aria-pressed', 'true');
    filterCards();
  });
});

const copyText = (txt) => {
  if (navigator.clipboard && window.isSecureContext) {
    navigator.clipboard.writeText(txt).then(() => {
      flash('Link copied!');
    }).catch(() => {
      fallbackCopy(txt);
    });
  } else {
    fallbackCopy(txt);
  }
};

const fallbackCopy = (txt) => {
  const ta = document.createElement('textarea');
  ta.value = txt;
  ta.style.position = 'fixed';
  ta.style.opacity = '0';
  document.body.appendChild(ta);
  ta.focus();
  ta.select();
  try {
    document.execCommand('copy');
    flash('Copied!');
  } catch (err) {
    flash('Failed to copy');
  }
  document.body.removeChild(ta);
};

const flash = (msg) => {
  let t = document.getElementById('toast');
  if (!t) {
    t = document.createElement('div');
    t.id = 'toast';
    document.body.appendChild(t);
  }
  t.textContent = msg;
  t.style.opacity = '1';
  clearTimeout(t._timeout);
  t._timeout = setTimeout(() => {
    t.style.opacity = '0';
  }, 2000);
};

document.getElementById('year').textContent = new Date().getFullYear();

document.querySelectorAll('.navlinks a[href^="#"]').forEach(a => {
  a.addEventListener('click', (e) => {
    document.querySelectorAll('.navlinks a').forEach(x => {
      x.classList.remove('active');
      x.removeAttribute('aria-current');
    });
    a.classList.add('active');
    a.setAttribute('aria-current', 'page');
  });
});
